import React, { useState, useEffect, useCallback } from 'react';
import { Server, Zap, DollarSign, Cloud, Leaf, TreePine, Sun, Wind, Recycle, Cpu, HardDrive, Building, Settings } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

// Types
interface Improvement {
  id: string;
  name: string;
  description: string;
  basePrice: number;
  currentPrice: number;
  count: number;
  dollarEffect: number;
  pollutionEffect: number;
  icon: React.ElementType;
  type: 'ai' | 'eco';
  effectType?: 'passive' | 'instant';
}

interface GameState {
  dollars: number;
  pollution: number;
  clickPower: number;
  passiveIncome: number;
  pollutionRate: number;
  improvements: Improvement[];
}

// Store using React state (Zustand-like pattern)
const useGameStore = () => {
  const [gameState, setGameState] = useState<GameState>({
    dollars: 0,
    pollution: 0,
    clickPower: 1,
    passiveIncome: 0,
    pollutionRate: 0,
    improvements: [
      // AI Improvements
      {
        id: 'algo-opt',
        name: 'Optimisation d\'algorithme',
        description: '+0.5$/s, +0.3g CO‚ÇÇ/s',
        basePrice: 50,
        currentPrice: 50,
        count: 0,
        dollarEffect: 0.5,
        pollutionEffect: 0.3,
        icon: Settings,
        type: 'ai'
      },
      {
        id: 'new-gpu',
        name: 'Nouveau GPU',
        description: '+2$/s, +1g CO‚ÇÇ/s',
        basePrice: 150,
        currentPrice: 150,
        count: 0,
        dollarEffect: 2,
        pollutionEffect: 1,
        icon: Cpu,
        type: 'ai'
      },
      {
        id: 'server-cluster',
        name: 'Cluster de serveurs',
        description: '+10$/s, +6g CO‚ÇÇ/s',
        basePrice: 800,
        currentPrice: 800,
        count: 0,
        dollarEffect: 10,
        pollutionEffect: 6,
        icon: HardDrive,
        type: 'ai'
      },
      {
        id: 'data-center',
        name: 'Data center additionnel',
        description: '+30$/s, +15g CO‚ÇÇ/s',
        basePrice: 2000,
        currentPrice: 2000,
        count: 0,
        dollarEffect: 30,
        pollutionEffect: 15,
        icon: Building,
        type: 'ai'
      },
      // Eco Improvements
      {
        id: 'plant-tree',
        name: 'Planter un arbre',
        description: '-0.1g CO‚ÇÇ/s',
        basePrice: 40,
        currentPrice: 40,
        count: 0,
        dollarEffect: 0,
        pollutionEffect: -0.1,
        icon: TreePine,
        type: 'eco',
        effectType: 'passive'
      },
      {
        id: 'solar-panel',
        name: 'Installer panneau solaire',
        description: '-0.3g CO‚ÇÇ/s',
        basePrice: 100,
        currentPrice: 100,
        count: 0,
        dollarEffect: 0,
        pollutionEffect: -0.3,
        icon: Sun,
        type: 'eco',
        effectType: 'passive'
      },
      {
        id: 'windmill',
        name: '√âolienne',
        description: '-1g CO‚ÇÇ/s',
        basePrice: 400,
        currentPrice: 400,
        count: 0,
        dollarEffect: 0,
        pollutionEffect: -1,
        icon: Wind,
        type: 'eco',
        effectType: 'passive'
      },
      {
        id: 'thermal-recycling',
        name: 'Recyclage thermique serveur',
        description: '-5g CO‚ÇÇ instantan√©',
        basePrice: 200,
        currentPrice: 200,
        count: 0,
        dollarEffect: 0,
        pollutionEffect: -5,
        icon: Recycle,
        type: 'eco',
        effectType: 'instant'
      }
    ]
  });

  const addDollars = useCallback((amount: number) => {
    setGameState(prev => ({ ...prev, dollars: prev.dollars + amount }));
  }, []);

  const addPollution = useCallback((amount: number) => {
    setGameState(prev => ({ 
      ...prev, 
      pollution: Math.max(0, prev.pollution + amount)
    }));
  }, []);

  const buyImprovement = useCallback((improvementId: string) => {
    setGameState(prev => {
      const improvement = prev.improvements.find(imp => imp.id === improvementId);
      if (!improvement || prev.dollars < improvement.currentPrice) return prev;

      const newImprovements = prev.improvements.map(imp => {
        if (imp.id === improvementId) {
          const newCount = imp.count + 1;
          const newPrice = Math.floor(imp.basePrice * Math.pow(1.15, newCount));
          return { ...imp, count: newCount, currentPrice: newPrice };
        }
        return imp;
      });

      // Calculate new passive values
      let newPassiveIncome = 0;
      let newPollutionRate = 0;

      newImprovements.forEach(imp => {
        if (imp.type === 'ai') {
          newPassiveIncome += imp.dollarEffect * imp.count;
          newPollutionRate += imp.pollutionEffect * imp.count;
        } else if (imp.type === 'eco' && imp.effectType === 'passive') {
          newPollutionRate += imp.pollutionEffect * imp.count; // negative values reduce pollution
        }
      });

      // Handle instant effects
      let pollutionChange = 0;
      if (improvement.type === 'eco' && improvement.effectType === 'instant') {
        pollutionChange = improvement.pollutionEffect;
      }

      return {
        ...prev,
        dollars: prev.dollars - improvement.currentPrice,
        pollution: Math.max(0, prev.pollution + pollutionChange),
        improvements: newImprovements,
        passiveIncome: newPassiveIncome,
        pollutionRate: newPollutionRate
      };
    });
  }, []);

  return { gameState, addDollars, addPollution, buyImprovement };
};

// Helper functions
const formatNumber = (num: number): string => {
  if (num >= 1000000) return `${(num / 1000000).toFixed(1)}M`;
  if (num >= 1000) return `${(num / 1000).toFixed(1)}K`;
  return Math.floor(num).toString();
};

const formatPollution = (grams: number): string => {
  if (grams >= 1000000) return `${(grams / 1000000).toFixed(1)}T`;
  if (grams >= 1000) return `${(grams / 1000).toFixed(1)}kg`;
  return `${Math.floor(grams)}g`;
};

const getPollutionLevel = (pollution: number): { level: number; color: string; emoji: string; status: string } => {
  const maxPollution = 1000; // Arbitrary max for percentage calculation
  const percentage = (pollution / maxPollution) * 100;

  if (percentage <= 25) return { level: 0, color: 'text-green-500', emoji: 'üåç', status: '√âquilibr√©' };
  if (percentage <= 50) return { level: 1, color: 'text-yellow-500', emoji: 'üå§Ô∏è', status: 'Sous tension' };
  if (percentage <= 75) return { level: 2, color: 'text-orange-500', emoji: 'üåã', status: 'Critique' };
  return { level: 3, color: 'text-red-500', emoji: 'üíÄ', status: 'Catastrophique' };
};

// Components
const ClickButton: React.FC<{ onClick: () => void }> = ({ onClick }) => {
  const [clickEffect, setClickEffect] = useState(false);

  const handleClick = () => {
    onClick();
    setClickEffect(true);
    setTimeout(() => setClickEffect(false), 150);
  };

  return (
    <motion.div
      className="relative flex justify-center items-center"
      whileTap={{ scale: 0.95 }}
    >
      <motion.button
        onClick={handleClick}
        className={`relative w-32 h-32 rounded-full bg-gradient-to-br from-blue-500 to-purple-600 
                   shadow-lg hover:shadow-xl transition-all duration-200 border-4 border-white
                   ${clickEffect ? 'animate-pulse' : ''}`}
        whileHover={{ scale: 1.05 }}
      >
        <Server className="w-16 h-16 text-white mx-auto" />

        {clickEffect && (
          <motion.div
            initial={{ scale: 0, opacity: 1 }}
            animate={{ scale: 2, opacity: 0 }}
            transition={{ duration: 0.3 }}
            className="absolute inset-0 border-2 border-yellow-400 rounded-full"
          />
        )}
      </motion.button>

      <AnimatePresence>
        {clickEffect && (
          <motion.div
            initial={{ y: 0, opacity: 1 }}
            animate={{ y: -30, opacity: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.5 }}
            className="absolute top-0 text-green-500 font-bold text-xl pointer-events-none"
          >
            +$1
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

const StatsPanel: React.FC<{ gameState: GameState }> = ({ gameState }) => {
  const pollutionData = getPollutionLevel(gameState.pollution);

  return (
    <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {/* Dollars */}
        <div className="flex items-center space-x-3">
          <DollarSign className="w-8 h-8 text-green-500" />
          <div>
            <div className="text-2xl font-bold text-green-600">
              ${formatNumber(gameState.dollars)}
            </div>
            <div className="text-sm text-gray-500">
              +${gameState.passiveIncome.toFixed(1)}/s
            </div>
          </div>
        </div>

        {/* Pollution */}
        <div className="flex items-center space-x-3">
          <Cloud className="w-8 h-8 text-gray-500" />
          <div>
            <div className="text-2xl font-bold text-gray-700">
              {formatPollution(gameState.pollution)}
            </div>
            <div className="text-sm text-gray-500">
              {gameState.pollutionRate >= 0 ? '+' : ''}{gameState.pollutionRate.toFixed(1)}g/s
            </div>
          </div>
        </div>

        {/* Environmental Impact */}
        <div className="flex items-center space-x-3">
          <div className="text-3xl">{pollutionData.emoji}</div>
          <div>
            <div className={`text-xl font-bold ${pollutionData.color}`}>
              {pollutionData.status}
            </div>
            <div className="w-32 h-2 bg-gray-200 rounded-full overflow-hidden">
              <div 
                className={`h-full transition-all duration-500 ${
                  pollutionData.level === 0 ? 'bg-green-500' :
                  pollutionData.level === 1 ? 'bg-yellow-500' :
                  pollutionData.level === 2 ? 'bg-orange-500' : 'bg-red-500'
                }`}
                style={{ width: `${Math.min(100, (gameState.pollution / 1000) * 100)}%` }}
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const ImprovementCard: React.FC<{ 
  improvement: Improvement; 
  canAfford: boolean; 
  onBuy: () => void;
}> = ({ improvement, canAfford, onBuy }) => {
  const Icon = improvement.icon;
  const isAI = improvement.type === 'ai';

  return (
    <motion.div
      className={`p-4 rounded-lg border-2 transition-all duration-200 ${
        canAfford 
          ? `border-${isAI ? 'blue' : 'green'}-300 bg-${isAI ? 'blue' : 'green'}-50 hover:shadow-md cursor-pointer` 
          : 'border-gray-300 bg-gray-100 opacity-50 cursor-not-allowed'
      }`}
      whileHover={canAfford ? { scale: 1.02 } : {}}
      onClick={canAfford ? onBuy : undefined}
    >
      <div className="flex items-center justify-between mb-2">
        <div className="flex items-center space-x-2">
          <Icon className={`w-6 h-6 ${isAI ? 'text-blue-600' : 'text-green-600'}`} />
          <span className="font-semibold text-gray-800">{improvement.name}</span>
        </div>
        {improvement.count > 0 && (
          <span className="bg-gray-200 text-gray-700 px-2 py-1 rounded-full text-sm">
            {improvement.count}
          </span>
        )}
      </div>

      <div className="text-sm text-gray-600 mb-2">
        {improvement.description}
      </div>

      <div className="flex justify-between items-center">
        <span className="text-lg font-bold text-green-600">
          ${formatNumber(improvement.currentPrice)}
        </span>
        {canAfford && (
          <motion.button
            className={`px-3 py-1 rounded ${
              isAI ? 'bg-blue-500 hover:bg-blue-600' : 'bg-green-500 hover:bg-green-600'
            } text-white text-sm`}
            whileTap={{ scale: 0.95 }}
          >
            Acheter
          </motion.button>
        )}
      </div>
    </motion.div>
  );
};

const ImprovementsPanel: React.FC<{ 
  improvements: Improvement[]; 
  dollars: number; 
  onBuy: (id: string) => void;
}> = ({ improvements, dollars, onBuy }) => {
  const [activeTab, setActiveTab] = useState<'ai' | 'eco'>('ai');

  const aiImprovements = improvements.filter(imp => imp.type === 'ai');
  const ecoImprovements = improvements.filter(imp => imp.type === 'eco');

  return (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <div className="flex mb-4">
        <button
          className={`px-4 py-2 rounded-l-lg font-semibold transition-colors ${
            activeTab === 'ai' 
              ? 'bg-blue-500 text-white' 
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
          onClick={() => setActiveTab('ai')}
        >
          <Cpu className="w-4 h-4 inline mr-2" />
          Am√©liorations IA
        </button>
        <button
          className={`px-4 py-2 rounded-r-lg font-semibold transition-colors ${
            activeTab === 'eco' 
              ? 'bg-green-500 text-white' 
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
          onClick={() => setActiveTab('eco')}
        >
          <Leaf className="w-4 h-4 inline mr-2" />
          Solutions Vertes
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {(activeTab === 'ai' ? aiImprovements : ecoImprovements).map(improvement => (
          <ImprovementCard
            key={improvement.id}
            improvement={improvement}
            canAfford={dollars >= improvement.currentPrice}
            onBuy={() => onBuy(improvement.id)}
          />
        ))}
      </div>
    </div>
  );
};

// Main Game Component
const AIClickerGame: React.FC = () => {
  const { gameState, addDollars, addPollution, buyImprovement } = useGameStore();

  // Passive income effect
  useEffect(() => {
    const interval = setInterval(() => {
      if (gameState.passiveIncome > 0) {
        addDollars(gameState.passiveIncome / 10); // Divide by 10 for smoother updates (100ms intervals)
      }
      if (gameState.pollutionRate !== 0) {
        addPollution(gameState.pollutionRate / 10);
      }
    }, 100);

    return () => clearInterval(interval);
  }, [gameState.passiveIncome, gameState.pollutionRate, addDollars, addPollution]);

  const handleClick = () => {
    addDollars(gameState.clickPower);
    addPollution(0.1); // Small pollution per click
  };

  const pollutionData = getPollutionLevel(gameState.pollution);
  const backgroundClass = 
    pollutionData.level === 0 ? 'bg-gradient-to-br from-blue-100 to-green-100' :
    pollutionData.level === 1 ? 'bg-gradient-to-br from-yellow-100 to-orange-100' :
    pollutionData.level === 2 ? 'bg-gradient-to-br from-orange-200 to-red-200' :
    'bg-gradient-to-br from-red-300 to-gray-400';

  return (
    <div className={`min-h-screen transition-all duration-1000 ${backgroundClass}`}>
      <div className="container mx-auto px-4 py-8">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-800 mb-2">
            L'IA contre la Plan√®te
          </h1>
          <p className="text-gray-600">
            D√©veloppez votre empire IA... mais √† quel prix √©cologique ?
          </p>
        </div>

        <StatsPanel gameState={gameState} />

        <div className="flex justify-center mb-8">
          <ClickButton onClick={handleClick} />
        </div>

        <ImprovementsPanel
          improvements={gameState.improvements}
          dollars={gameState.dollars}
          onBuy={buyImprovement}
        />

        <div className="text-center mt-8 text-sm text-gray-600">
          <p>Une critique ludique de l'impact √©cologique des intelligences artificielles</p>
        </div>
      </div>
    </div>
  );
};

export default AIClickerGame;
