import { AnimatePresence, motion } from "framer-motion";
import {
  Building,
  Cpu,
  Droplet,
  HardDrive,
  Leaf,
  Recycle,
  Server,
  Settings,
  Sun,
  TreePine,
  Wind,
  AlertTriangle,
} from "lucide-react";
import React, { useCallback, useEffect, useState } from "react";

// Types
interface Improvement {
  id: string;
  name: string;
  description: string;
  basePrice: number;
  currentPrice: number;
  count: number;
  dollarEffect: number;
  pollutionEffect: number;
  icon: React.ElementType;
  type: "ai" | "eco" | "optimization";
  effectType?: "passive" | "instant";
  waterEffect?: number;
  powerEffect?: number;
  requestEffect?: number;
}

interface GameState {
  dollars: number;
  pollution: number;
  clickPower: number;
  passiveIncome: number;
  pollutionRate: number;
  improvements: Improvement[];
  kilowatts: number;
  waterLiters: number;
  aiRequests: number;
  currentDay: number;
  currentHour: number;
  currentMinute: number;
}

// Messages pÃ©dagogiques
const educationalMessages = [
  "Une requÃªte GPT-3 peut consommer autant qu'une ampoule allumÃ©e pendant une heure",
  "L'IA gÃ©nÃ©rative nÃ©cessite des fermes de serveurs entiÃ¨res refroidies Ã  l'eau potable",
  "Un entraÃ®nement de GPT-3 a Ã©mis autant de COâ‚‚ qu'un aller-retour Parisâ€“New York",
  "Une seule image gÃ©nÃ©rÃ©e par IA peut consommer plus d'Ã©lectricitÃ© qu'un smartphone utilisÃ© toute une journÃ©e",
  "Les fermes de serveurs consomment des millions de litres d'eau potable pour leur refroidissement",
  "MÃªme en veille, les IA consomment de l'Ã©nergie pour rester prÃªtes Ã  vous rÃ©pondre"
];

// CSS Styles
const styles = {
  container: {
    minHeight: "100vh",
    paddingTop: "70px", // Espace pour le bandeau
    padding: "70px 16px 32px 16px",
    fontFamily: "system-ui, -apple-system, sans-serif",
    transition: "all 1s ease",
  },
  banner: {
    position: "fixed" as const,
    top: "0",
    left: "0",
    right: "0",
    height: "50px",
    background: "linear-gradient(90deg, #DC2626, #EA580C, #D97706, #DC2626)",
    backgroundSize: "200% 100%",
    animation: "gradientShift 4s ease-in-out infinite",
    color: "white",
    display: "flex",
    alignItems: "center",
    overflow: "hidden",
    zIndex: 1000,
    boxShadow: "0 2px 8px rgba(0,0,0,0.2)",
  },
  bannerContent: {
    display: "flex",
    alignItems: "center",
    whiteSpace: "nowrap" as const,
    animation: "scroll 25s linear infinite",
    fontSize: "14px",
    fontWeight: "500",
    paddingLeft: "100%",
  },
  bannerIcon: {
    marginRight: "12px",
    flexShrink: 0,
  },
  bannerText: {
    marginRight: "100px", // Espacement entre les messages
  },
  header: {
    textAlign: "center" as const,
    marginBottom: "32px",
  },
  title: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    color: "#374151",
    marginBottom: "8px",
  },
  subtitle: {
    color: "#6B7280",
    fontSize: "1rem",
  },
  statsPanel: {
    background: "white",
    borderRadius: "8px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    padding: "24px",
    marginBottom: "24px",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "16px",
  },
  statItem: {
    display: "flex",
    alignItems: "center",
    gap: "12px",
  },
  statValue: {
    fontSize: "1.5rem",
    fontWeight: "bold",
  },
  statRate: {
    fontSize: "0.875rem",
    color: "#6B7280",
  },
  clickButtonContainer: {
    display: "flex",
