    padding: "16px",
    marginBottom: "24px",
    borderRadius: "8px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
  },
  realWorldStatsTitle: {
    fontSize: "1.25rem",
    fontWeight: "bold",
    marginBottom: "16px",
    textAlign: "center" as const,
    color: "#94A3B8",
  },
  realWorldStatsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
    gap: "16px",
  },
  realWorldStatItem: {
    display: "flex",
    alignItems: "center",
    gap: "12px",
    padding: "8px",
    background: "rgba(255, 255, 255, 0.1)",
